package Assignment2;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int n = 24;
			if(n%2==0)
			{
				System.out.println("the even number is "+n);
			}
			else
			{
				System.out.println("the odd number is "+n);
			}
	}

}
